<?php//$Id: bureau.class.php,v 1.7 2008-07-29 14:00:45 jbastide Exp $include ("../dyn/var.inc");require_once ($path_om."formulairedyn.class.php");require_once ($path_om."dbformdyn.class.php");//class bureau extends dbForm{    var $table = "bureau";    var $clePrimaire = "bureau";    var $typeCle = "A";    function bureau ($id, $db, $debug)    {        $this -> constructeur ($id, $db, $debug);    } // param_bureau ($id, $db, $debug)    function verifier ()    {        $this -> correct = True;        $imgv="";        $f="&nbsp!&nbsp;&nbsp;&nbsp;&nbsp;";        $imgv="<img src='../img/punaise.png' align='middle'  border='0'>";        if ($this -> valF ['bureau'] == "")        {            $this -> correct = false;            $this -> msg = $this -> msg.$imgv.$this->lang("code")."&nbsp;".$this->lang("bureau")."&nbsp".$this->lang("obligatoire").$f;        }        if ($this -> valF ['canton'] == "")        {            $this -> correct = false;            $this -> msg = $this -> msg.$imgv."&nbsp;".$this->lang("canton")."&nbsp".$this->lang("obligatoire").$f;        }        if ($this -> valF ['libelle'] == "")        {            $this -> correct = false;            $this -> msg = $this -> msg.$imgv.$this->lang("libelle")."&nbsp;".$this->lang("bureau")."&nbsp".$this->lang("obligatoire").$f;        }    } // verifier ()    function setType (&$form, $maj)     {        if ($maj < 2)        {            // Ajout            $form -> setType ('canton', 'select');            $form -> setType ('libelle', 'text');            if ($maj == 1)            {                // Modification                 $form -> setType ('bureau', 'hiddenstatic');            }        }        else        {             $form -> setType ('bureau', 'hiddenstatic');            // Suppression        }    } // setType (&$form, $maj)     function setSelect(&$form,$maj,$db,$DEBUG)    {        include ("../dyn/connexion.php");        include ("../sql/".$dsn['phptype']."/".$this -> table.".form.inc");                // Liste de choix : canton        $res = $db->query($sql_canton);        if (DB :: isError($res))            die($res->getMessage().$sql_canton);        else        {            if ($DEBUG == 1)                echo " la requete ".$sql_canton." est exécutée<br>";            $contenu [0][0] = "";            $contenu [1][0] = $this->lang("choisir")."&nbsp;".$this->lang("un")."&nbsp;".$this->lang("canton");            $k=1;            while ($row=& $res->fetchRow())            {                    $contenu[0][$k]=$row[0];                    $contenu[1][$k]=$row[1];                    $k++;            }            $form->setSelect('canton',$contenu);        }               } // setSelect(&$form,$maj,$db,$DEBUG)    function setMax(&$form,$maj)    {        $form->setMax('libelle', 40);    }    function setTaille(&$form,$maj)    {        $form->setTaille('libelle', 40);    }function setVal(&$form,$maj,$validation){if ($validation==0) {  if ($maj == 0){    include ("../dyn/var.inc");    $form->setVal('cp', $client_cp);    $form->setVal('ville', $client_ville);}}}  function cleSecondaire($id,$db,$val,$debug) {$this->rechercheAffectation($id,$db,$debug) ;$this->rechercheCandidature($id,$db,$debug) ;}function rechercheAffectation($id,$db,$debug){    $this->correct=True;    $imgv="";    $f="&nbsp!&nbsp;&nbsp;&nbsp;&nbsp;";    $imgv="<img src='../img/punaise.png' align='middle'  border='0'>";    $sql = "select * from affectation where bureau ='".$id."'";    $res = $db->query($sql);    if($debug==1) echo $sql;    if (DB :: isError($res))       die($res->getMessage(). " => Echec  ".$sql);    else{        $nbligne=$res->numrows();        $this->msg = $this->msg.$imgv."&nbsp;".$this->lang("il_y_a")."&nbsp;<font class='parametre'>&nbsp;".$nbligne."&nbsp;</font>&nbsp;".        $this->lang("enregistrement")."&nbsp;".$this->lang("en_lien")."&nbsp;".$this->lang("avec")."&nbsp;".$this->lang("table")." \"affectation\" ".$this->lang("pour")."&nbsp;".$this->lang("le")."&nbsp;".$this->lang("bureau")."&nbsp; [".        $id."]".$f."<br>";        if ($nbligne>0)           $this->correct=false;    }}function rechercheCandidature($id,$db,$debug){    $this->correct=True;    $imgv="";    $f="&nbsp!&nbsp;&nbsp;&nbsp;&nbsp;";    $imgv="<img src='../img/punaise.png' align='middle'  border='0'>";    $sql = "select * from candidature where bureau ='".$id."'";    $res = $db->query($sql);    if($debug==1) echo $sql;    if (DB :: isError($res))       die($res->getMessage(). " => Echec  ".$sql);    else{        $nbligne=$res->numrows();        $this->msg = $this->msg.$imgv."&nbsp;".$this->lang("il_y_a")."&nbsp;<font class='parametre'>&nbsp;".$nbligne."&nbsp;</font>&nbsp;".        $this->lang("enregistrement")."&nbsp;".$this->lang("en_lien")."&nbsp;".$this->lang("avec")."&nbsp;".$this->lang("table")." \"candidature\" ".$this->lang("pour")."&nbsp;".$this->lang("le")."&nbsp;".$this->lang("bureau")."&nbsp; [".        $id."]".$f."<br>";        if ($nbligne>0)           $this->correct=false;    }}}?>