    $nbligne=$res->numrows();        $this->msg = $this->msg.$imgv."&nbsp;".$this->lang("il_y_a")."&nbsp;<font class='parametre'>&nbsp;".$nbligne."&nbsp;</font>&nbsp;".        $this->lang("enregistrement")."&nbsp;".$this->lang("en_lien")."&nbsp;".$this->lang("avec")."&nbsp;".$this->lang("table")." \"agent\" ".$this->lang("pour")."&nbsp;".$this->lang("le")."&nbsp;".$this->lang("service")."&nbsp; [".        $id."]".$f."<br>";        if ($nbligne>0)           $this->correct=false;    }} }?>